left join (
  SELECT id from organization where org_type = 4 and del_status !=-1
)b on b.id = a.org_id
left join (
  SELECT agent,amount FROM protocol WHERE 1 = 1 and status in (1,2)
)c on c.agent = a.org_id
where b.id is not null and  c.agent is not NULL
