SELECT * from follow_up where disable !=0 and is_follow_up !=0
andn dept_id in

    #{item}

SELECT id from follow_up where disable !=0 and is_follow_up !=0
andn dept_id in

    #{item}

SELECT * from follow_up where is_follow_up !=0 and disable !=0 and id in

    #{item}


    and name=${followUp}

limit ${limitStart},${limitEnd}

SELECT count(0) from follow_up where is_follow_up !=0 and disable !=0 and id in

    #{item}


    and name=${followUp}

SELECT *,sum(d.amount)agentFund,count(DISTINCT c.id)agentNum from follow_up a
left join (
    select follow_id,agent_id from follow_agent
)b on b.follow_id =a.id
LEFT join (
    select id from agent where del_status!=-1
)c on c.id =b.agent_id
LEFT join (
    SELECT agent,amount FROM protocol where status in (1,2)
)d on d.agent = c.id
left join (
    SELECT  id from deparment
)e on e.id =a.dept_id
where a.is_follow_up !=0 and a.disable !=0

    and a.mobile =${mobile}

and a.id in

    #{item}


    and a.invite_code =${inviteCode}

GROUP BY a.id
limit ${limitStart},${limitEnd}

SELECT count(0) from follow_up where is_follow_up !=0 and disable !=0

    and mobile =${mobile}


and id in

    #{item}



    and inviteCode =${inviteCode}

SELECT id from follow_up
left join (
    select follow_id,agent_id,sum(d.amount)agentFund,count(DISTINCT c.id)agentNum from follow_agent
)b on b.follow_id =a.id
LEFT join (
    select id from agent where del_status!=-1
)c on c.id =b.agent_id
LEFT join (
    SELECT agent,amount FROM protocol where status in (1,2)
)d on d.agent = c.id
where  a.id in

    #{item}

  GROUP BY a.id

SELECT a.follow_id,a.follow_up_before,a.assign_date changeDate,a.agent_fund_before agentCargoBefore,
a.total_fund_before,b.id,b.link_person,b.mobile,b.create_date,b.record_invite_code,c.name followUp
from follow_agent a
left join (
    select * from #{assistDataBase}.agent where del_status !=-1
)b on b.id =a.agent_id
left join (
    select * from follow_up where is_follow_up!=0 and disable!=0
)c on c.id =a.follow_id
where a.disable!=0 and a.follow_id in

    #{item}


limit #{limitStart},#{limitEnd}

SELECT count(0)
from follow_agent a
left join (
select * from #{assistDataBase}.agent where del_status !=-1
)b on b.id =a.agent_id
left join (
select * from follow_up where is_follow_up!=0 and disable!=0
)c on c.id =a.follow_id
where a.disable!=0 and a.follow_id in

    #{item}

SELECT a.follow_id,a.follow_up_before,a.assign_date changeDate,a.manage_fund_before agentCargoBefore,
b.id,b.link_person,b.mobile,b.create_date,b.record_invite_code,c.name followUp
from follow_org a
left join (
select * from #{assistDataBase}.agent where del_status !=-1
)b on b.id =a.org_id
left join (
select * from follow_up where is_follow_up!=0 and disable!=0
)c on c.id =a.follow_id
where a.disable!=0 and a.follow_id in

    #{item}


limit #{limitStart},#{limitEnd}

SELECT count(0)
from follow_org a
left join (
select * from #{assistDataBase}.organization where del_status !=-1
)b on b.id =a.org_id
left join (
select * from follow_up where is_follow_up!=0 and disable!=0
)c on c.id =a.follow_id
where a.disable!=0 and a.follow_id in

