mapper.select:
SELECT * from follow_up_log
where follow_id =#{followId}
and create_date BETWEEN #{startDate} and #{endDate}
ORDER  by create_date desc
limit #{limitStart},#{limitEnd}

SELECT count(0) from follow_up_log
where follow_id =#{followId}
and create_date BETWEEN #{startDate} and #{endDate}
ORDER  by create_date desc
