(function(vds){

    window.vds = vds;

}(function(sid,uid,callback){

    var _vds = _vds || [];

    _vds.push(['setAccountId', sid]);

    _vds.push(['setCS1', 'user_id', uid||0]);

    (function(vds,s) {
        vds.type='text/javascript';
        vds.async = true;
        if(Object.prototype.toString.apply(callback)=='[object Function]'){
            vds.onload = callback;
        }
        vds.src = '//assets.growingio.com/vds.js';
        s.parentNode.insertBefore(vds, s);
    })(document.createElement('script'),document.getElementsByTagName('script')[0]);

    window._vds = _vds;

}));